namespace Shine
{
	/** 主角加技能消息(generated by shine) */
	export class MUnitAddSkillResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.MUnitAddSkill;
		
		/** 单位序号 */
		public index:number;
		
		/** 技能数据 */
		public skill:SkillData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.MUnitAddSkill;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.index=stream.readInt();
			
			this.skill=stream.readDataSimpleNotNull() as SkillData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
