namespace Shine
{
	/** 主角刷buff(generated by shine) */
	export class MUnitRefreshBuffResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.MUnitRefreshBuff;
		
		/** 单位序号 */
		public index:number;
		
		/** buff流水ID */
		public instanceID:number;
		
		/** 剩余时间 */
		public lastTime:number;
		
		/** 剩余次数 */
		public lastNum:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.MUnitRefreshBuff;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "MUnitRefreshBuffResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.index=stream.readInt();
			
			this.instanceID=stream.readInt();
			
			this.lastTime=stream.readInt();
			
			this.lastNum=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
