namespace Shine
{
	/** 玩家单位开始CD组(generated by shine) */
	export class MUnitStartCDsResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.MUnitStartCDs;
		
		/** 单位序号 */
		public index:number;
		
		/** 冷却组 */
		public cds:SList<CDData>;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.MUnitStartCDs;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "MUnitStartCDsResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.index=stream.readInt();
			
			var cdsLen:number=stream.readLen();
			if(this.cds!=null)
			{
				this.cds.clear();
				this.cds.ensureCapacity(cdsLen);
			}
			else
			{
				this.cds=new SList<CDData>();
			}
			
			var cdsT:SList<CDData>=this.cds;
			for(var cdsI:number=cdsLen-1;cdsI>=0;--cdsI)
			{
				var cdsV:CDData;
				cdsV=new CDData();
				cdsV.readBytesSimple(stream);
				
				cdsT.add(cdsV);
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
