namespace Shine
{
	/** 添加绑定视野单位(generated by shine) */
	export class AddBindVisionUnitResponse extends SceneSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.AddBindVisionUnit;
		
		/** 实例ID */
		public instanceID:number;
		
		/** 单位数据(如为空则为在视野中) */
		public data:UnitSimpleData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.AddBindVisionUnit;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.instanceID=stream.readInt();
			
			if(stream.readBoolean())
			{
				this.data=stream.readDataSimpleNotNull() as UnitSimpleData;
			}
			else
			{
				this.data=null;
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
