namespace Shine
{
	/** 离开场景消息(generated by shine) */
	export class LeaveSceneResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.LeaveScene;
		
		/** 是否还要进下一个场景 */
		public hasNextScene:boolean;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.LeaveScene;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "LeaveSceneResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.hasNextScene=stream.readBoolean();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
