namespace Shine
{
	/** 预备进入场景消息(generated by shine) */
	export class PreEnterSceneResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.PreEnterScene;
		
		/** 场景ID */
		public id:number;
		
		/** 线ID */
		public lineID:number=-1;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.PreEnterScene;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PreEnterSceneResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.lineID=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
