namespace Shine
{
	/** 推送副本类场景状态信息(generated by shine) */
	export class SendBattleStateResponse extends SceneSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendBattleState;
		
		/** 副本当前状态 */
		public state:number;
		
		/** 剩余tick时间(-1就是没在tick) */
		public timeTick:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendBattleState;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendBattleStateResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.state=stream.readInt();
			
			this.timeTick=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
