namespace Shine
{
	/** 单位发出子弹消息(generated by shine) */
	export class AddBulletResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.AddBullet;
		
		/** 子弹数据 */
		public bullet:BulletData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.AddBullet;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AddBulletResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.bullet=stream.readDataSimpleNotNull() as BulletData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
