namespace Shine
{
	/** 更新操作体状态消息(generated by shine) */
	export class RefreshOperationStateResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshOperationState;
		
		/** 状态 */
		public state:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshOperationState;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.state=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
