namespace Shine
{
	/** 移除子弹(generated by shine) */
	export class RemoveBulletResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RemoveBullet;
		
		/** 子弹实例ID */
		public bulletInstanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RemoveBullet;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RemoveBulletResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.bulletInstanceID=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
