namespace Shine
{
	/** 单位聊天消息(generated by shine) */
	export class UnitChatResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitChat;
		
		/** 聊天数据 */
		public data:ChatData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitChat;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "UnitChatResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as ChatData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
