namespace Shine
{
	/** 单位死亡消息(generated by shine) */
	export class UnitDeadResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitDead;
		
		/** 是否真死亡(否则就是死后复活) */
		public isReal:boolean;
		
		/** 击杀者实例ID */
		public attackerInstanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitDead;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "UnitDeadResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.attackerInstanceID=stream.readInt();
			
			this.isReal=stream.readBoolean();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
