namespace Shine
{
	/** 单位移动点组消息(generated by shine) */
	export class UnitMovePosListResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitMovePosList;
		
		/** 移动类型 */
		public type:number;
		
		/** 位置组 */
		public targets:SList<PosData>;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitMovePosList;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "UnitMovePosListResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.type=stream.readInt();
			
			var targetsLen:number=stream.readLen();
			if(this.targets!=null)
			{
				this.targets.clear();
				this.targets.ensureCapacity(targetsLen);
			}
			else
			{
				this.targets=new SList<PosData>();
			}
			
			var targetsT:SList<PosData>=this.targets;
			for(var targetsI:number=targetsLen-1;targetsI>=0;--targetsI)
			{
				var targetsV:PosData;
				targetsV=new PosData();
				targetsV.readBytesSimple(stream);
				
				targetsT.add(targetsV);
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
