namespace Shine
{
	/** 单位刷新buff(generated by shine) */
	export class UnitRefreshBuffResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitRefreshBuff;
		
		/** buff实例ID */
		public buffInstanceID:number;
		
		/** 剩余时间 */
		public lastTime:number;
		
		/** 剩余次数 */
		public lastNum:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitRefreshBuff;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "UnitRefreshBuffResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.buffInstanceID=stream.readInt();
			
			this.lastTime=stream.readInt();
			
			this.lastNum=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
