namespace Shine
{
	/** 单位直接更改位置朝向消息(generated by shine) */
	export class UnitSetPosDirResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitSetPosDir;
		
		/** 位置朝向 */
		public posDir:PosDirData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitSetPosDir;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.posDir=new PosDirData();
			this.posDir.readBytesSimple(stream);
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
