namespace Shine
{
	/** 单位同步指令消息(generated by shine) */
	export class UnitSyncCommandResponse extends UnitSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.UnitSyncCommand;
		
		/** 当前位置 */
		public posDir:PosDirData;
		
		/** 指令 */
		public type:number;
		
		/** 整形参数组 */
		public ints:number[];
		
		/** float参数组 */
		public floats:number[];
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.UnitSyncCommand;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "UnitSyncCommandResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.posDir=new PosDirData();
			this.posDir.readBytesSimple(stream);
			
			this.type=stream.readInt();
			
			var intsLen:number=stream.readLen();
			this.ints=new Array<number>(intsLen);
			var intsT:number[]=this.ints;
			for(var intsI:number=0;intsI<intsLen;++intsI)
			{
				var intsV:number;
				intsV=stream.readInt();
				
				intsT[intsI]=intsV;
			}
			
			var floatsLen:number=stream.readLen();
			this.floats=new Array<number>(floatsLen);
			var floatsT:number[]=this.floats;
			for(var floatsI:number=0;floatsI<floatsLen;++floatsI)
			{
				var floatsV:number;
				floatsV=stream.readFloat();
				
				floatsT[floatsI]=floatsV;
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
