namespace Shine
{
	/** 回复查询角色消息(generated by shine) */
	export class ReQueryPlayerResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.ReQueryPlayer;
		
		/** 回复角色数据 */
		public result:QueryPlayerResultData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ReQueryPlayer;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ReQueryPlayerResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.result=stream.readDataSimpleNotNull() as QueryPlayerResultData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.social.onQueryPlayerResult(this.result);
		}
		
	}
}
