namespace Shine
{
	/** 推送添加好友黑名单(generated by shine) */
	export class SendAddFriendBlackListResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendAddFriendBlackList;
		
		/** 联系人数据 */
		public data:ContactData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendAddFriendBlackList;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendAddFriendBlackListResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as ContactData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.friend.onAddBlackList(this.data);
		}
		
	}
}
