namespace Shine
{
	/** 推送添加好友消息(generated by shine) */
	export class SendAddFriendResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendAddFriend;
		
		/** 好友数据 */
		public data:FriendData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendAddFriend;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendAddFriendResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as FriendData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.friend.onAddFriend(this.data);
		}
		
	}
}
