		public bytes:number[];
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ClientHotfixConfig;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			var bytesLen:number=stream.readLen();
			this.bytes=new Array<number>(bytesLen);
			var bytesT:number[]=this.bytes;
			for(var bytesI:number=0;bytesI<bytesLen;++bytesI)
			{
				var bytesV:number;
				bytesV=stream.readByte();
				
				bytesT[bytesI]=bytesV;
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			// GameC.config.loadHotfix(this.bytes);
			// GameC.main.onReloadConfig();
		}
		
