namespace Shine
{
	/** 每日消息(generated by shine) */
	export class DailyResponse extends GameResponse
	{
		/** 下个每日时间 */
		public nextDailyTime:number;
		
		/** 数据类型ID */
		public static dataID:number=GameResponseType.Daily;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.Daily;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "DailyResponse";
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.onDaily();
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.nextDailyTime=stream.readLong();
			
		}
		
	}
}
