namespace Shine
{
	/** 刷新服务器时间(generated by shine) */
	export class RefreshServerTimeResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshServerTime;
		
		/** 当前时间(ms) */
		public currentTime:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshServerTime;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefreshServerTimeResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.currentTime=stream.readLong();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.system.serServerTime(this.currentTime);
		}
		
	}
}
