namespace Shine
{
	/** 推送信息码消息(generated by shine) */
	export class SendInfoCodeResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendInfoCode;
		
		/** 码 */
		public code:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendInfoCode;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendInfoCodeResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.code=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			GameC.info.toShowInfoCode(this.code,true,null);
		}
		
	}
}
