namespace Shine
{
	/** 双整形数据(generated by shine) */
	export class DIntData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=ShineDataType.DInt;
		
		/** 键 */
		public key:number=-1;
		
		/** 值 */
		public value:number=0;
		
		constructor()
		{
			super();
			this._dataID=ShineDataType.DInt;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "DIntData";
		}
		
		/** 读取字节流(完整版) */
		protected toReadBytesFull(stream:BytesReadStream):void
		{
			var position:number=BytesReadStream.getReadBytes(stream);
			
			this.key=stream.readInt();
			
			this.value=stream.readInt();
			
			BytesReadStream.disReadBytes(stream,position);
		}
		
		/** 写入字节流(完整版) */
		protected toWriteBytesFull(stream:BytesWriteStream):void
		{
			var position:number=BytesWriteStream.getWriteBytes(stream);
			
			stream.writeInt(this.key);
			
			stream.writeInt(this.value);
			
			BytesWriteStream.disWriteBytes(stream,position);
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.key=stream.readInt();
			
			this.value=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.key);
			
			stream.writeInt(this.value);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof DIntData))
				return;
			
			var mData:DIntData=data as DIntData;
			
			this.key=mData.key;
			this.value=mData.value;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof DIntData))
				return;
			
			var mData:DIntData=data as DIntData;
			
			this.key=mData.key;
			
			this.value=mData.value;
			
		}
		
		/** 是否数据一致 */
		protected toDataEquals(data:BaseData):boolean
		{
			var mData:DIntData=data as DIntData;
			if(this.key!=mData.key)
				return false;
			
			if(this.value!=mData.value)
				return false;
			
			return true;
		}
		
		/** 转文本输出 */
		protected toWriteDataString(writer:DataWriter):void
		{
			writer.writeTabs();
			writer.sb.append("key");
			writer.sb.append(':');
			writer.sb.append(this.key);
			
			writer.writeEnter();
			writer.writeTabs();
			writer.sb.append("value");
			writer.sb.append(':');
			writer.sb.append(this.value);
			
			writer.writeEnter();
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
		/** 是否为空 */
		public isEmpty():boolean
		{
			return this.key<=0 && this.value<=0;
		}
		
	}
}
