namespace Shine
{
	/** 重连成功(generated by shine) */
	export class SocketReconnectSuccessRequest extends ShineRequest
	{
		/** 数据类型ID */
		public static dataID:number=ShineRequestType.SocketReconnectSuccess;
		
		/** 最后收包索引 */
		public lastReceiveIndex:number;
		
		constructor()
		{
			super();
			this._dataID=ShineRequestType.SocketReconnectSuccess;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SocketReconnectSuccessRequest";
		}
		
		/** 写入字节流(完整版) */
		protected toWriteBytesFull(stream:BytesWriteStream):void
		{
			super.toWriteBytesFull(stream);
			
			var position:number=BytesWriteStream.getWriteBytes(stream);
			
			stream.writeInt(this.lastReceiveIndex);
			
			BytesWriteStream.disWriteBytes(stream,position);
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.lastReceiveIndex);
			
		}
		
		/** 转文本输出 */
		protected toWriteDataString(writer:DataWriter):void
		{
			super.toWriteDataString(writer);
			
			writer.writeTabs();
			writer.sb.append("lastReceiveIndex");
			writer.sb.append(':');
			writer.sb.append(this.lastReceiveIndex);
			
			writer.writeEnter();
		}
		
		/** 创建实例 */
		public static createSocketReconnectSuccess(lastReceiveIndex:number):SocketReconnectSuccessRequest
		{
			var re:SocketReconnectSuccessRequest=new SocketReconnectSuccessRequest();
			re.lastReceiveIndex=lastReceiveIndex;
			return re;
		}
		
	}
}
