namespace Shine
{
	/** (generated by shine) */
	export class PingResponse extends ShineResponse
	{
		/** 数据类型ID */
		public static dataID:number=ShineResponseType.Ping;
		
		constructor()
		{
			super();
			this._dataID=ShineResponseType.Ping;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PingResponse";
		}
		
		/** 读取字节流(完整版) */
		protected toReadBytesFull(stream:BytesReadStream):void
		{
			super.toReadBytesFull(stream);
			
			var position:number=BytesReadStream.getReadBytes(stream);
			
			BytesReadStream.disReadBytes(stream,position);
		}
		
		/** 执行 */
		protected execute():void
		{
			Ctrl.errorLog("客户端不该受到ping消息");
			// this.socket.refreshPingTime();
			// this.socket.sendAbs(RePingRequest.create());
		}
		
	}
}
