namespace Shine
{
	/** 重连成功(generated by shine) */
	export class SocketReconnectSuccessResponse extends ShineResponse
	{
		/** 数据类型ID */
		public static dataID:number=ShineResponseType.SocketReconnectSuccess;
		
		/** 最后收包索引 */
		public lastReceiveIndex:number;
		
		constructor()
		{
			super();
			this._dataID=ShineResponseType.SocketReconnectSuccess;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SocketReconnectSuccessResponse";
		}
		
		/** 读取字节流(完整版) */
		protected toReadBytesFull(stream:BytesReadStream):void
		{
			super.toReadBytesFull(stream);
			
			var position:number=BytesReadStream.getReadBytes(stream);
			
			this.lastReceiveIndex=stream.readInt();
			
			BytesReadStream.disReadBytes(stream,position);
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.lastReceiveIndex=stream.readInt();
			
		}
		
		/** 转文本输出 */
		protected toWriteDataString(writer:DataWriter):void
		{
			super.toWriteDataString(writer);
			
			writer.writeTabs();
			writer.sb.append("lastReceiveIndex");
			writer.sb.append(':');
			writer.sb.append(this.lastReceiveIndex);
			
			writer.writeEnter();
		}
		
		/** 执行 */
		protected execute():void
		{
			this.socket.onReconnectSuccess(this.lastReceiveIndex);
		}
		
	}
}
