package com.home.commonClient.net.centerResponse.system;
import com.home.commonClient.constlist.generate.CenterResponseType;
import com.home.commonClient.net.base.CenterResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 中心服推送信息码(generated by shine) */
public class SendInfoCodeFromCenterResponse extends CenterResponse
{
	/** 数据类型ID */
	public static final int dataID=CenterResponseType.SendInfoCodeFromCenter;
	
	/** 错误码 */
	public int code;
	
	public SendInfoCodeFromCenterResponse()
	{
		_dataID=CenterResponseType.SendInfoCodeFromCenter;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.system.onInfoCode(code);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendInfoCodeFromCenterResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.code=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.code=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("code");
		writer.sb.append(':');
		writer.sb.append(this.code);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.code=0;
	}
	
}
