package com.home.commonClient.net.httpRequest;
import com.home.commonClient.constlist.generate.LoginHttpRequestType;
import com.home.commonClient.net.base.LoginHttpRequest;
import com.home.commonClient.net.httpResponseResult.ClientGetVersionResult;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;

/** 客户端获取版本消息(generated by shine) */
public class ClientGetVersionHttpRequest extends LoginHttpRequest
{
	/** 数据类型ID */
	public static final int dataID=LoginHttpRequestType.ClientGetVersion;
	
	/** 当前资源版本号(用来做url重定向) */
	public int resourceVersion;
	
	/** 返回值 */
	protected ClientGetVersionResult _re;
	
	/** 客户端平台类型(见ClientPlatformType) */
	public int platformType;
	
	public ClientGetVersionHttpRequest()
	{
		_dataID=LoginHttpRequestType.ClientGetVersion;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 执行 */
	@Override
	protected void toRead()
	{
		_re=new ClientGetVersionResult();
		readResult(_re,_resultStream);
	}
	
	/** 执行 */
	@Override
	protected void onComplete()
	{
		
	}
	
	/** 获取结果 */
	public ClientGetVersionResult getResult()
	{
		return _re;
	}
	
	/** 同步执行 */
	public ClientGetVersionResult sendMSync()
	{
		doSendSync();
		return _re;
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.platformType);
		
		stream.writeInt(this.resourceVersion);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.platformType);
		
		stream.writeInt(this.resourceVersion);
		
	}
	
	/** 创建实例 */
	public static ClientGetVersionHttpRequest create(int platformType,int resourceVersion)
	{
		ClientGetVersionHttpRequest re=new ClientGetVersionHttpRequest();
		re.platformType=platformType;
		re.resourceVersion=resourceVersion;
		return re;
	}
	
}
