package com.home.commonClient.net.request.func.aution;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.request.func.base.FuncRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 取消上架拍卖行物品消息(generated by shine) */
public class FuncAuctionCancelSellItemRequest extends FuncRRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncAuctionCancelSellItem;
	
	/** 实例id */
	public long instanceID;
	
	public FuncAuctionCancelSellItemRequest()
	{
		_dataID=GameRequestType.FuncAuctionCancelSellItem;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncAuctionCancelSellItemRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeLong(this.instanceID);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.instanceID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.instanceID=0L;
	}
	
	/** 创建实例 */
	public static FuncAuctionCancelSellItemRequest create(int funcID,long instanceID)
	{
		FuncAuctionCancelSellItemRequest re=(FuncAuctionCancelSellItemRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.instanceID=instanceID;
		return re;
	}
	
}
