package com.home.commonClient.net.request.func.roleGroup;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.request.func.base.FuncRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 处理邀请玩家群消息(generated by shine) */
public class FuncHandleInviteRoleGroupRequest extends FuncRRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncHandleInviteRoleGroup;
	
	/** 群id */
	public long groupID;
	
	/** 邀请者 */
	public long invitor;
	
	/** 结果 */
	public int result;
	
	public FuncHandleInviteRoleGroupRequest()
	{
		_dataID=GameRequestType.FuncHandleInviteRoleGroup;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncHandleInviteRoleGroupRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeLong(this.invitor);
		
		stream.writeLong(this.groupID);
		
		stream.writeInt(this.result);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.invitor);
		
		stream.writeLong(this.groupID);
		
		stream.writeInt(this.result);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("invitor");
		writer.sb.append(':');
		writer.sb.append(this.invitor);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("groupID");
		writer.sb.append(':');
		writer.sb.append(this.groupID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("result");
		writer.sb.append(':');
		writer.sb.append(this.result);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.invitor=0L;
		this.groupID=0L;
		this.result=0;
	}
	
	/** 创建实例 */
	public static FuncHandleInviteRoleGroupRequest create(int funcID,long invitor,long groupID,int result)
	{
		FuncHandleInviteRoleGroupRequest re=(FuncHandleInviteRoleGroupRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.invitor=invitor;
		re.groupID=groupID;
		re.result=result;
		return re;
	}
	
}
