package com.home.commonClient.net.request.login;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 角色绑定平台消息(generated by shine) */
public class ApplyBindPlatformRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ApplyBindPlatform;
	
	public String uid="";
	
	public String platform="";
	
	public ApplyBindPlatformRequest()
	{
		_dataID=GameRequestType.ApplyBindPlatform;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ApplyBindPlatformRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("uid");
		writer.sb.append(':');
		writer.sb.append(this.uid);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("platform");
		writer.sb.append(':');
		writer.sb.append(this.platform);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeUTF(this.uid);
		
		stream.writeUTF(this.platform);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.uid);
		
		stream.writeUTF(this.platform);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.uid="";
		this.platform="";
	}
	
	/** 创建实例 */
	public static ApplyBindPlatformRequest create(String uid,String platform)
	{
		ApplyBindPlatformRequest re=(ApplyBindPlatformRequest)BytesControl.createRequest(dataID);
		re.uid=uid;
		re.platform=platform;
		return re;
	}
	
}
