package com.home.commonClient.net.request.login;
import com.home.commonBase.data.login.CreatePlayerData;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.commonClient.net.request.base.LoginRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 创建角色消息(generated by shine) */
public class CreatePlayerRequest extends LoginRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.CreatePlayer;
	
	/** 数据 */
	public CreatePlayerData data;
	
	public CreatePlayerRequest()
	{
		_dataID=GameRequestType.CreatePlayer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		CreatePlayerData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(CreatePlayerData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CreatePlayerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("CreatePlayerData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static CreatePlayerRequest create(CreatePlayerData data)
	{
		CreatePlayerRequest re=(CreatePlayerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
