package com.home.commonClient.net.request.login;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.commonClient.net.request.base.LoginRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 角色切换game服消息(generated by shine) */
public class PlayerSwitchGameRequest extends LoginRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.PlayerSwitchGame;
	
	/** 令牌 */
	public int token;
	
	public PlayerSwitchGameRequest()
	{
		_dataID=GameRequestType.PlayerSwitchGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerSwitchGameRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("token");
		writer.sb.append(':');
		writer.sb.append(this.token);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.token);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.token);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.token=0;
	}
	
	/** 创建实例 */
	public static PlayerSwitchGameRequest create(int token)
	{
		PlayerSwitchGameRequest re=(PlayerSwitchGameRequest)BytesControl.createRequest(dataID);
		re.token=token;
		return re;
	}
	
}
