package com.home.commonClient.net.request.mail;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;

/** 获取全部邮件消息(generated by shine) */
public class GetAllMailRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.GetAllMail;
	
	public GetAllMailRequest()
	{
		_dataID=GameRequestType.GetAllMail;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "GetAllMailRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	/** 创建实例 */
	public static GetAllMailRequest create()
	{
		GetAllMailRequest re=(GetAllMailRequest)BytesControl.createRequest(dataID);
		return re;
	}
	
}
