package com.home.commonClient.net.request.mail;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 邮件移除红点(generated by shine) */
public class MailRemoveRedPointRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.MailRemoveRedPoint;
	
	/** 邮件序号 */
	public int index;
	
	public MailRemoveRedPointRequest()
	{
		_dataID=GameRequestType.MailRemoveRedPoint;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MailRemoveRedPointRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("index");
		writer.sb.append(':');
		writer.sb.append(this.index);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.index);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.index);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.index=0;
	}
	
	/** 创建实例 */
	public static MailRemoveRedPointRequest create(int index)
	{
		MailRemoveRedPointRequest re=(MailRemoveRedPointRequest)BytesControl.createRequest(dataID);
		re.index=index;
		return re;
	}
	
}
