package com.home.commonClient.net.request.role;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 申请更改角色名消息(generated by shine) */
public class ApplyChangeRoleNameRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ApplyChangeRoleName;
	
	/** 名字 */
	public String name="";
	
	public ApplyChangeRoleNameRequest()
	{
		_dataID=GameRequestType.ApplyChangeRoleName;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ApplyChangeRoleNameRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("name");
		writer.sb.append(':');
		writer.sb.append(this.name);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeUTF(this.name);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.name);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.name="";
	}
	
	/** 创建实例 */
	public static ApplyChangeRoleNameRequest create(String name)
	{
		ApplyChangeRoleNameRequest re=(ApplyChangeRoleNameRequest)BytesControl.createRequest(dataID);
		re.name=name;
		return re;
	}
	
}
