package com.home.commonClient.net.request.role;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.commonClient.net.request.role.MUnitRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 主单位切换属性开关(generated by shine) */
public class MUnitAttributeSwitchNormalSendRequest extends MUnitRRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.MUnitAttributeSwitchNormalSend;
	
	/** 是否开启 */
	public boolean isOpen;
	
	public MUnitAttributeSwitchNormalSendRequest()
	{
		_dataID=GameRequestType.MUnitAttributeSwitchNormalSend;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MUnitAttributeSwitchNormalSendRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("isOpen");
		writer.sb.append(':');
		writer.sb.append(this.isOpen);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeBoolean(this.isOpen);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeBoolean(this.isOpen);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.isOpen=false;
	}
	
	/** 创建实例 */
	public static MUnitAttributeSwitchNormalSendRequest create(int mIndex,boolean isOpen)
	{
		MUnitAttributeSwitchNormalSendRequest re=(MUnitAttributeSwitchNormalSendRequest)BytesControl.createRequest(dataID);
		re.mIndex=mIndex;
		re.isOpen=isOpen;
		return re;
	}
	
}
