package com.home.commonClient.net.request.scene.unit;
import com.home.commonBase.data.scene.base.PosData;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.request.scene.base.CUnitRRequest;
import com.home.commonClient.net.request.scene.base.SceneRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.SList;
import com.home.shine.support.pool.DataPool;

/** 客户端单位移动到点组消息(generated by shine) */
public class CUnitMovePosListRequest extends CUnitRRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.CUnitMovePosList;
	
	/** 移动类型 */
	public int type;
	
	/** 当前位置 */
	public PosData nowPos;
	
	/** 位置组 */
	public SList<PosData> targets;
	
	public CUnitMovePosListRequest()
	{
		_dataID=GameRequestType.CUnitMovePosList;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PosData nowPosTemp=nowPos;
		if(nowPosTemp!=null)
		{
			this.nowPos=new PosData();
			this.nowPos.copy(nowPosTemp);
		}
		else
		{
			this.nowPos=null;
		}
		
		SList<PosData> targetsTemp=targets;
		if(targetsTemp!=null)
		{
			this.targets=new SList<PosData>(PosData[]::new,targetsTemp.size());
			SList<PosData> targetsT=this.targets;
			if(!targetsTemp.isEmpty())
			{
				Object[] targetsVValues=targetsTemp.getValues();
				for(int targetsVI=0,targetsVLen=targetsTemp.length();targetsVI<targetsVLen;++targetsVI)
				{
					PosData targetsV=(PosData)targetsVValues[targetsVI];
					PosData targetsU;
					if(targetsV!=null)
					{
						targetsU=new PosData();
						targetsU.copy(targetsV);
					}
					else
					{
						targetsU=null;
						nullObjError("targetsU");
					}
					
					targetsT.add(targetsU);
				}
			}
		}
		else
		{
			this.targets=null;
			nullObjError("targets");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitMovePosListRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("nowPos");
		writer.sb.append(':');
		if(this.nowPos!=null)
		{
			this.nowPos.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PosData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("targets");
		writer.sb.append(':');
		writer.sb.append("List<PosData>");
		if(this.targets!=null)
		{
			SList<PosData> targetsT=this.targets;
			int targetsLen=targetsT.size();
			writer.sb.append('(');
			writer.sb.append(targetsLen);
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			for(int targetsI=0;targetsI<targetsLen;++targetsI)
			{
				PosData targetsV=targetsT.get(targetsI);
				writer.writeTabs();
				writer.sb.append(targetsI);
				writer.sb.append(':');
				if(targetsV!=null)
				{
					targetsV.writeDataString(writer);
				}
				else
				{
					writer.sb.append("PosData=null");
				}
				
				writer.writeEnter();
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.type);
		
		if(this.nowPos!=null)
		{
			stream.writeBoolean(true);
			this.nowPos.writeBytesFull(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.targets!=null)
		{
			stream.writeLen(this.targets.size());
			if(!this.targets.isEmpty())
			{
				Object[] targetsVValues=this.targets.getValues();
				for(int targetsVI=0,targetsVLen=this.targets.length();targetsVI<targetsVLen;++targetsVI)
				{
					PosData targetsV=(PosData)targetsVValues[targetsVI];
					if(targetsV!=null)
					{
						targetsV.writeBytesFull(stream);
					}
					else
					{
						nullObjError("targetsV");
					}
					
				}
			}
		}
		else
		{
			nullObjError("targets");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.type);
		
		if(this.nowPos!=null)
		{
			stream.writeBoolean(true);
			this.nowPos.writeBytesSimple(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.targets!=null)
		{
			stream.writeLen(this.targets.size());
			if(!this.targets.isEmpty())
			{
				Object[] targetsVValues=this.targets.getValues();
				for(int targetsVI=0,targetsVLen=this.targets.length();targetsVI<targetsVLen;++targetsVI)
				{
					PosData targetsV=(PosData)targetsVValues[targetsVI];
					if(targetsV!=null)
					{
						targetsV.writeBytesSimple(stream);
					}
					else
					{
						nullObjError("targetsV");
					}
					
				}
			}
		}
		else
		{
			nullObjError("targets");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.type=0;
		if(this.nowPos!=null)
		{
			this.nowPos.release(pool);
			this.nowPos=null;
		}
		if(this.targets!=null)
		{
			if(!this.targets.isEmpty())
			{
				Object[] targetsVValues=this.targets.getValues();
				for(int targetsVI=0,targetsVLen=this.targets.length();targetsVI<targetsVLen;++targetsVI)
				{
					PosData targetsV=(PosData)targetsVValues[targetsVI];
					targetsV.release(pool);
					targetsV=null;
					targetsVValues[targetsVI]=null;
				}
			}
			this.targets.justClearSize();
		}
	}
	
	/** 创建实例 */
	public static CUnitMovePosListRequest create(int instanceID,int type,PosData nowPos,SList<PosData> targets)
	{
		CUnitMovePosListRequest re=(CUnitMovePosListRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.type=type;
		re.nowPos=nowPos;
		re.targets=targets;
		return re;
	}
	
}
