package com.home.commonClient.net.request.scene.unit;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.request.scene.base.CUnitRRequest;
import com.home.commonClient.net.request.scene.base.SceneRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拾取物品消息(generated by shine) */
public class CUnitPickUpItemRequest extends CUnitRRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.CUnitPickUpItem;
	
	/** 目标物品实例ID */
	public int targetInstanceID;
	
	public CUnitPickUpItemRequest()
	{
		_dataID=GameRequestType.CUnitPickUpItem;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitPickUpItemRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.targetInstanceID);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.targetInstanceID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("targetInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.targetInstanceID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.targetInstanceID=0;
	}
	
	/** 创建实例 */
	public static CUnitPickUpItemRequest create(int instanceID,int targetInstanceID)
	{
		CUnitPickUpItemRequest re=(CUnitPickUpItemRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.targetInstanceID=targetInstanceID;
		return re;
	}
	
}
