package com.home.commonClient.net.request.social.chat;
import com.home.commonBase.data.social.chat.ChatData;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家聊天消息(generated by shine) */
public class PlayerChatRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.PlayerChat;
	
	/** 聊天数据 */
	public ChatData data;
	
	/** 频道 */
	public int channel;
	
	/** 二级索引 */
	public long key;
	
	public PlayerChatRequest()
	{
		_dataID=GameRequestType.PlayerChat;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		ChatData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(ChatData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerChatRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.writeInt(this.channel);
		
		stream.writeLong(this.key);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.writeInt(this.channel);
		
		stream.writeLong(this.key);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ChatData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("channel");
		writer.sb.append(':');
		writer.sb.append(this.channel);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
		this.channel=0;
		this.key=0L;
	}
	
	/** 创建实例 */
	public static PlayerChatRequest create(ChatData data,int channel,long key)
	{
		PlayerChatRequest re=(PlayerChatRequest)BytesControl.createRequest(dataID);
		re.data=data;
		re.channel=channel;
		re.key=key;
		return re;
	}
	
}
