package com.home.commonClient.net.response.func.item;
import com.home.commonBase.data.item.ItemData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.commonClient.net.response.func.base.FuncSResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.IntIntMap;
import com.home.shine.support.collection.IntObjectMap;
import com.home.shine.support.pool.DataPool;

/** 添加物品消息(generated by shine) */
public class FuncAddItemResponse extends FuncSResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.FuncAddItem;
	
	/** 途径 */
	public int way;
	
	/** 自动使用物品组 */
	public IntIntMap autoUseItems;
	
	/** 更新组 */
	public IntObjectMap<ItemData> items;
	
	public FuncAddItemResponse()
	{
		_dataID=GameResponseType.FuncAddItem;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.func.getBaseItemContainerTool(funcID).onAddItemsByServer(autoUseItems,items,way);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncAddItemResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.way=stream.readInt();
		
		if(stream.readBoolean())
		{
			int autoUseItemsLen=stream.readLen();
			if(this.autoUseItems!=null)
			{
				this.autoUseItems.clear();
				this.autoUseItems.ensureCapacity(autoUseItemsLen);
			}
			else
			{
				this.autoUseItems=new IntIntMap(autoUseItemsLen);
			}
			
			IntIntMap autoUseItemsT=this.autoUseItems;
			for(int autoUseItemsI=autoUseItemsLen-1;autoUseItemsI>=0;--autoUseItemsI)
			{
				int autoUseItemsK;
				int autoUseItemsV;
				autoUseItemsK=stream.readInt();
				
				autoUseItemsV=stream.readInt();
				
				autoUseItemsT.put(autoUseItemsK,autoUseItemsV);
			}
		}
		else
		{
			this.autoUseItems=null;
		}
		
		if(stream.readBoolean())
		{
			int itemsLen=stream.readLen();
			if(this.items!=null)
			{
				this.items.clear();
				this.items.ensureCapacity(itemsLen);
			}
			else
			{
				this.items=new IntObjectMap<ItemData>(ItemData[]::new,itemsLen);
			}
			
			IntObjectMap<ItemData> itemsT=this.items;
			for(int itemsI=itemsLen-1;itemsI>=0;--itemsI)
			{
				int itemsK;
				ItemData itemsV;
				itemsK=stream.readInt();
				
				if(stream.readBoolean())
				{
					BaseData itemsVT=stream.readDataFullNotNull();
					if(itemsVT!=null)
					{
						if(itemsVT instanceof ItemData)
						{
							itemsV=(ItemData)itemsVT;
						}
						else
						{
							itemsV=new ItemData();
							if(!(itemsVT.getClass().isAssignableFrom(ItemData.class)))
							{
								stream.throwTypeReadError(ItemData.class,itemsVT.getClass());
							}
							itemsV.shadowCopy(itemsVT);
						}
					}
					else
					{
						itemsV=null;
					}
				}
				else
				{
					itemsV=null;
				}
				
				itemsT.put(itemsK,itemsV);
			}
		}
		else
		{
			this.items=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.way=stream.readInt();
		
		if(stream.readBoolean())
		{
			int autoUseItemsLen=stream.readLen();
			if(this.autoUseItems!=null)
			{
				this.autoUseItems.clear();
				this.autoUseItems.ensureCapacity(autoUseItemsLen);
			}
			else
			{
				this.autoUseItems=new IntIntMap(autoUseItemsLen);
			}
			
			IntIntMap autoUseItemsT=this.autoUseItems;
			for(int autoUseItemsI=autoUseItemsLen-1;autoUseItemsI>=0;--autoUseItemsI)
			{
				int autoUseItemsK;
				int autoUseItemsV;
				autoUseItemsK=stream.readInt();
				
				autoUseItemsV=stream.readInt();
				
				autoUseItemsT.put(autoUseItemsK,autoUseItemsV);
			}
		}
		else
		{
			this.autoUseItems=null;
		}
		
		if(stream.readBoolean())
		{
			int itemsLen=stream.readLen();
			if(this.items!=null)
			{
				this.items.clear();
				this.items.ensureCapacity(itemsLen);
			}
			else
			{
				this.items=new IntObjectMap<ItemData>(ItemData[]::new,itemsLen);
			}
			
			IntObjectMap<ItemData> itemsT=this.items;
			for(int itemsI=itemsLen-1;itemsI>=0;--itemsI)
			{
				int itemsK;
				ItemData itemsV;
				itemsK=stream.readInt();
				
				if(stream.readBoolean())
				{
					itemsV=(ItemData)stream.readDataSimpleNotNull();
				}
				else
				{
					itemsV=null;
				}
				
				itemsT.put(itemsK,itemsV);
			}
		}
		else
		{
			this.items=null;
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("way");
		writer.sb.append(':');
		writer.sb.append(this.way);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("autoUseItems");
		writer.sb.append(':');
		writer.sb.append("Map<int,int>");
		if(this.autoUseItems!=null)
		{
			writer.sb.append('(');
			writer.sb.append(this.autoUseItems.size());
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.autoUseItems.isEmpty())
			{
				int autoUseItemsKFreeValue=this.autoUseItems.getFreeValue();
				long[] autoUseItemsKTable=this.autoUseItems.getTable();
				long autoUseItemsKEntry;
				for(int autoUseItemsKI=autoUseItemsKTable.length-1;autoUseItemsKI>=0;--autoUseItemsKI)
				{
					autoUseItemsKEntry=autoUseItemsKTable[autoUseItemsKI];
					int autoUseItemsK=(int)autoUseItemsKEntry;
					if(autoUseItemsK!=autoUseItemsKFreeValue)
					{
						int autoUseItemsV=(int)(autoUseItemsKEntry>>>32);
						writer.writeTabs();
						writer.sb.append(autoUseItemsK);
						
						writer.sb.append(':');
						writer.sb.append(autoUseItemsV);
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("items");
		writer.sb.append(':');
		writer.sb.append("Map<int,ItemData>");
		if(this.items!=null)
		{
			writer.sb.append('(');
			writer.sb.append(this.items.size());
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.items.isEmpty())
			{
				int itemsKFreeValue=this.items.getFreeValue();
				int[] itemsKKeys=this.items.getKeys();
				Object[] itemsVValues=this.items.getValues();
				for(int itemsKI=itemsKKeys.length-1;itemsKI>=0;--itemsKI)
				{
					int itemsK=itemsKKeys[itemsKI];
					if(itemsK!=itemsKFreeValue)
					{
						ItemData itemsV=(ItemData)itemsVValues[itemsKI];
						writer.writeTabs();
						writer.sb.append(itemsK);
						
						writer.sb.append(':');
						if(itemsV!=null)
						{
							itemsV.writeDataString(writer);
						}
						else
						{
							writer.sb.append("ItemData=null");
						}
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.way=0;
		this.autoUseItems=null;
		this.items=null;
	}
	
}
