package com.home.commonClient.net.response.login;
import com.home.commonBase.data.login.PlayerLoginData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 创建角色成功消息(generated by shine) */
public class CreatePlayerSuccessResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.CreatePlayerSuccess;
	
	public PlayerLoginData role;
	
	public CreatePlayerSuccessResponse()
	{
		_dataID=GameResponseType.CreatePlayerSuccess;
	}
	
	/** 执行 */
	protected void execute()
	{
		me.system.onCreatePlayerSuccess(role);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CreatePlayerSuccessResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData roleT=stream.readDataFullNotNull();
		if(roleT!=null)
		{
			if(roleT instanceof PlayerLoginData)
			{
				this.role=(PlayerLoginData)roleT;
			}
			else
			{
				this.role=new PlayerLoginData();
				if(!(roleT.getClass().isAssignableFrom(PlayerLoginData.class)))
				{
					stream.throwTypeReadError(PlayerLoginData.class,roleT.getClass());
				}
				this.role.shadowCopy(roleT);
			}
		}
		else
		{
			this.role=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.role=(PlayerLoginData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("role");
		writer.sb.append(':');
		if(this.role!=null)
		{
			this.role.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerLoginData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.role=null;
	}
	
}
