package com.home.commonClient.net.response.login;
import com.home.commonBase.data.login.ClientLoginServerInfoData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 切换game服消息(generated by shine) */
public class SwitchGameResponse extends GameResponse
{
	/** 服务器目标 */
	public ClientLoginServerInfoData info;
	
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SwitchGame;
	
	public SwitchGameResponse()
	{
		_dataID=GameResponseType.SwitchGame;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.system.onSwitchGame(info);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SwitchGameResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData infoT=stream.readDataFullNotNull();
		if(infoT!=null)
		{
			if(infoT instanceof ClientLoginServerInfoData)
			{
				this.info=(ClientLoginServerInfoData)infoT;
			}
			else
			{
				this.info=new ClientLoginServerInfoData();
				if(!(infoT.getClass().isAssignableFrom(ClientLoginServerInfoData.class)))
				{
					stream.throwTypeReadError(ClientLoginServerInfoData.class,infoT.getClass());
				}
				this.info.shadowCopy(infoT);
			}
		}
		else
		{
			this.info=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.info=(ClientLoginServerInfoData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("info");
		writer.sb.append(':');
		if(this.info!=null)
		{
			this.info.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ClientLoginServerInfoData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.info=null;
	}
	
}
