package com.home.commonClient.net.response.mail;
import com.home.commonBase.data.mail.MailData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.SList;
import com.home.shine.support.pool.DataPool;

/** 回复获取全部邮件消息(generated by shine) */
public class ReGetAllMailResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.ReGetAllMail;
	
	/** 数据 */
	public SList<MailData> list;
	
	public ReGetAllMailResponse()
	{
		_dataID=GameResponseType.ReGetAllMail;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReGetAllMailResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		int listLen=stream.readLen();
		if(this.list!=null)
		{
			this.list.clear();
			this.list.ensureCapacity(listLen);
		}
		else
		{
			this.list=new SList<MailData>(MailData[]::new,listLen);
		}
		
		SList<MailData> listT=this.list;
		for(int listI=listLen-1;listI>=0;--listI)
		{
			MailData listV;
			BaseData listVT=stream.readDataFullNotNull();
			if(listVT!=null)
			{
				if(listVT instanceof MailData)
				{
					listV=(MailData)listVT;
				}
				else
				{
					listV=new MailData();
					if(!(listVT.getClass().isAssignableFrom(MailData.class)))
					{
						stream.throwTypeReadError(MailData.class,listVT.getClass());
					}
					listV.shadowCopy(listVT);
				}
			}
			else
			{
				listV=null;
			}
			
			listT.add(listV);
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		int listLen=stream.readLen();
		if(this.list!=null)
		{
			this.list.clear();
			this.list.ensureCapacity(listLen);
		}
		else
		{
			this.list=new SList<MailData>(MailData[]::new,listLen);
		}
		
		SList<MailData> listT=this.list;
		for(int listI=listLen-1;listI>=0;--listI)
		{
			MailData listV;
			listV=(MailData)stream.readDataSimpleNotNull();
			
			listT.add(listV);
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("list");
		writer.sb.append(':');
		writer.sb.append("List<MailData>");
		if(this.list!=null)
		{
			SList<MailData> listT=this.list;
			int listLen=listT.size();
			writer.sb.append('(');
			writer.sb.append(listLen);
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			for(int listI=0;listI<listLen;++listI)
			{
				MailData listV=listT.get(listI);
				writer.writeTabs();
				writer.sb.append(listI);
				writer.sb.append(':');
				if(listV!=null)
				{
					listV.writeDataString(writer);
				}
				else
				{
					writer.sb.append("MailData=null");
				}
				
				writer.writeEnter();
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.list=null;
	}
	
}
