package com.home.commonClient.net.response.quest;
import com.home.commonBase.data.quest.AchievementData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送添加成就消息(generated by shine) */
public class SendAcceptAchievementResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SendAcceptAchievement;
	
	/** 成就数据 */
	public AchievementData data;
	
	public SendAcceptAchievementResponse()
	{
		_dataID=GameResponseType.SendAcceptAchievement;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendAcceptAchievementResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof AchievementData)
			{
				this.data=(AchievementData)dataT;
			}
			else
			{
				this.data=new AchievementData();
				if(!(dataT.getClass().isAssignableFrom(AchievementData.class)))
				{
					stream.throwTypeReadError(AchievementData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(AchievementData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("AchievementData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.achievement.onAcceptAchievementByServer(data);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
}
