package com.home.commonClient.net.response.role.munit;
import com.home.commonBase.data.scene.base.SkillData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.commonClient.net.response.role.munit.MUnitSResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 主角加技能消息(generated by shine) */
public class MUnitAddSkillResponse extends MUnitSResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.MUnitAddSkill;
	
	/** 技能数据 */
	public SkillData skill;
	
	public MUnitAddSkillResponse()
	{
		_dataID=GameResponseType.MUnitAddSkill;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MUnitAddSkillResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData skillT=stream.readDataFullNotNull();
		if(skillT!=null)
		{
			if(skillT instanceof SkillData)
			{
				this.skill=(SkillData)skillT;
			}
			else
			{
				this.skill=new SkillData();
				if(!(skillT.getClass().isAssignableFrom(SkillData.class)))
				{
					stream.throwTypeReadError(SkillData.class,skillT.getClass());
				}
				this.skill.shadowCopy(skillT);
			}
		}
		else
		{
			this.skill=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.skill=(SkillData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("skill");
		writer.sb.append(':');
		if(this.skill!=null)
		{
			this.skill.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SkillData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		mLogic.getFightLogic().addSkill(skill);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.skill=null;
	}
	
}
