package com.home.commonClient.net.response.role.munit;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.logic.unit.MUnitUseLogic;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.ctrl.Ctrl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 主单位消息基类(generated by shine) */
public class MUnitSResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.MUnitS;
	
	/** 主单位序号 */
	public int mIndex;
	
	/** 主单位逻辑 */
	public MUnitUseLogic mLogic;
	
	public MUnitSResponse()
	{
		_dataID=GameResponseType.MUnitS;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MUnitSResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.mIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.mIndex=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("mIndex");
		writer.sb.append(':');
		writer.sb.append(this.mIndex);
		
		writer.writeEnter();
	}
	
	@Override
	protected void preExecute()
	{
		if((mLogic=me.character.getMUnitUseLogic(mIndex))==null)
		{
			Ctrl.warnLog("收到MUnit类消息时,主单位使用逻辑不存在");
			return;
		}
		
		execute();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.mIndex=0;
	}
	
}
