package com.home.commonClient.net.response.scene;
import com.home.commonBase.data.scene.scene.SceneEnterData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 进入场景消息(依赖MessageCopy)(generated by shine) */
public class EnterSceneResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.EnterScene;
	
	/** 进入数据 */
	public SceneEnterData enterData;
	
	public EnterSceneResponse()
	{
		_dataID=GameResponseType.EnterScene;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "EnterSceneResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.scene.onEnterScene(enterData);
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData enterDataT=stream.readDataFullNotNull();
		if(enterDataT!=null)
		{
			if(enterDataT instanceof SceneEnterData)
			{
				this.enterData=(SceneEnterData)enterDataT;
			}
			else
			{
				this.enterData=new SceneEnterData();
				if(!(enterDataT.getClass().isAssignableFrom(SceneEnterData.class)))
				{
					stream.throwTypeReadError(SceneEnterData.class,enterDataT.getClass());
				}
				this.enterData.shadowCopy(enterDataT);
			}
		}
		else
		{
			this.enterData=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.enterData=(SceneEnterData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("enterData");
		writer.sb.append(':');
		if(this.enterData!=null)
		{
			this.enterData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneEnterData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.enterData=null;
	}
	
}
