package com.home.commonClient.net.response.scene.scene;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 离开场景消息(generated by shine) */
public class LeaveSceneResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.LeaveScene;
	
	/** 是否还要进下一个场景 */
	public boolean hasNextScene;
	
	public LeaveSceneResponse()
	{
		_dataID=GameResponseType.LeaveScene;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LeaveSceneResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.scene.onLeaveScene(hasNextScene);
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.hasNextScene=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.hasNextScene=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("hasNextScene");
		writer.sb.append(':');
		writer.sb.append(this.hasNextScene);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.hasNextScene=false;
	}
	
}
